import './App.css';
import {useState} from "react";

function App() {
  const [buttonClicks, setButtonClicks] = useState({
    button1: 0,
    button2: 0,
    button3: 0,
    button4: 0,
  });

  const handleButtonClick = (button) => {
    setButtonClicks((prevClicks) => ({
      ...prevClicks,
      [button]: prevClicks[button] + 1,
    }));
  };

  return (
      <div className="App">
        <h1>React App</h1>
        <div>
          <button onClick={() => handleButtonClick('button1')}>Button 1</button>
          <button onClick={() => handleButtonClick('button2')}>Button 2</button>
          <button onClick={() => handleButtonClick('button3')}>Button 3</button>
          <button onClick={() => handleButtonClick('button4')}>Button 4</button>
        </div>
        <div>
          <p>Button 1 Clicks: {buttonClicks.button1}</p>
          <p>Button 2 Clicks: {buttonClicks.button2}</p>
          <p>Button 3 Clicks: {buttonClicks.button3}</p>
          <p>Button 4 Clicks: {buttonClicks.button4}</p>
        </div>
      </div>
  );
}

export default App;
