import './App.css';
import { useState } from 'react';
import DataDisplay from './DataDisplay';

function App() {
    const [dataFromServer, setDataFromServer] = useState(null);
    const [newDataInput, setNewDataInput] = useState('');

    const handleButtonClick = async (button) => {
        try {
            switch (button) {
                // ... (existing cases remain unchanged)

                case 'button2':
                    // Insert data
                    await fetch('http://localhost:8080/api/employees', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ newData: newDataInput }),
                    });
                    break;

                // ... (other cases remain unchanged)

                default:
                    break;
            }
        } catch (error) {
            console.error(`Error handling ${button} click:`, error);
        }
    };

    return (
        <div className="App">
            <h1>React Four Buttons</h1>
            <div>
                <button onClick={() => handleButtonClick('button1')}>Button 1</button>
                <button onClick={() => handleButtonClick('button2')}>Button 2</button>
                <button onClick={() => handleButtonClick('button3')}>Button 3</button>
                <button onClick={() => handleButtonClick('button4')}>Button 4</button>
            </div>

            {dataFromServer && <DataDisplay data={dataFromServer} />}

            {/* Add a form for posting data */}
            <div className="postDataForm">
                <h2>Post Data</h2>
                <form
                    onSubmit={(e) => {
                        e.preventDefault();
                        handleButtonClick('button2');
                    }}
                >
                    <label>
                        New Data:
                        <input
                            type="text"
                            value={newDataInput}
                            onChange={(e) => setNewDataInput(e.target.value)}
                        />
                    </label>
                    <button type="submit">Post Data</button>
                </form>
            </div>
        </div>
    );
}

export default App;
