import './App.css';
import {useState} from "react";
import DataDisplay from "./DataDisplay";

function App() {
    const [dataFromServer, setDataFromServer] = useState(null);

    const handleButtonClick = async (button) => {
        try {
            switch (button) {
                case 'button1':
                    // Fetch data
                    const response = await fetch('http://localhost:8080/api/employees');
                    const data = await response.json();
                    setDataFromServer(data);
                    break;

                case 'button2':
                    // Insert data (replace with your backend API endpoint for inserting)
                    await fetch('http://localhost:8080/api/employees', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ newData: 'Replace with your data' }),
                    });
                    break;

                case 'button3':
                    // Update data (replace with your backend API endpoint for updating)
                    await fetch('http://localhost:8080/api/employees', {
                        method: 'PUT',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ updatedData: 'Replace with your data' }),
                    });
                    break;

                case 'button4':
                    // Delete data (replace with your backend API endpoint for deleting)
                    await fetch('employees/{employeeid}', {
                        method: 'DELETE',
                    });
                    break;

                default:
                    break;
            }
        } catch (error) {
            console.error(`Error handling ${button} click:`, error);
        }
    };

    return (
        <div className="App">
            <h1>React Four Buttons</h1>
            <div>
                <button onClick={() => handleButtonClick('button1')}>Button 1</button>
                <button onClick={() => handleButtonClick('button2')}>Button 2</button>
                <button onClick={() => handleButtonClick('button3')}>Button 3</button>
                <button onClick={() => handleButtonClick('button4')}>Button 4</button>
            </div>
            {dataFromServer && <DataDisplay data={dataFromServer} />} {/* Render the new component */}
        </div>
    );
};

export default App;
