import './App.css';
import { useState } from 'react';
import DataDisplay from './DataDisplay';

function App() {
    const [dataFromServer, setDataFromServer] = useState(null);
    const [newData, setNewData] = useState('');

    const handleButtonClick = async (button) => {
        try {
            switch (button) {
                case 'button1':
                    // Fetch data
                    const response = await fetch('http://localhost:8080/api/employees');
                    const data = await response.json();
                    setDataFromServer(data);
                    break;

                case 'button2':
                    // Insert data
                    await postData('http://localhost:8080/api/employees', { newData });
                    break;

                // ... (other cases remain unchanged)

                default:
                    break;
            }
        } catch (error) {
            console.error(`Error handling ${button} click:`, error);
        }
    };

    const postData = async (url, payload) => {
        try {
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(payload),
            });

            const data = await response.json();
            setDataFromServer(data);
        } catch (error) {
            console.error('Error posting data:', error);
        }
    };

    return (
        <div className="App">
            <h1>React Four Buttons</h1>
            <div>
                <button onClick={() => handleButtonClick('button1')}>Button 1</button>
                <button onClick={() => handleButtonClick('button2')}>Button 2</button>
                {/* ... (other buttons remain unchanged) */}
            </div>
            {dataFromServer && <DataDisplay data={dataFromServer} />}

            {/* Input box for posting data */}
            <div>
                <h2>Post Data</h2>
                <input
                    type="text"
                    value={newData}
                    onChange={(e) => setNewData(e.target.value)}
                    placeholder="Enter data to post"
                />
                <button onClick={() => handleButtonClick('button2')}>Post Data</button>
            </div>
        </div>
    );
}

export default App;
